// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

contract CrowdfundingRegistry is Ownable {
    address public latestVersion;
    
    event NewVersionRegistered(address indexed newVersion);

    function registerNewVersion(address _newVersion) external onlyOwner {
        latestVersion = _newVersion;
        emit NewVersionRegistered(_newVersion);
    }
}

contract Crowdfunding is ReentrancyGuard {
    struct Campaign {
        address payable creator;
        string title;
        uint256 goal;
        uint256 deadline;
        uint256 amountRaised;
        bool fundsReleased;
    }

    // Auto-generated campaign IDs
    mapping(bytes32 => Campaign) public campaigns;
    mapping(bytes32 => mapping(address => uint256)) public contributions;
    mapping(bytes32 => address[]) public backers;
    mapping(bytes32 => mapping(address => uint256)) public pendingRefunds;

    CrowdfundingRegistry public immutable registry;
    
    event CampaignCreated(
        bytes32 indexed campaignId, 
        address indexed creator, 
        string title, 
        uint256 goal, 
        uint256 deadline
    );
    
    event ContributionMade(
        bytes32 indexed campaignId, 
        address indexed backer, 
        uint256 amount
    );
    
    event FundsReleased(bytes32 indexed campaignId, uint256 amount);
    event RefundAvailable(bytes32 indexed campaignId, address indexed backer, uint256 amount);

    constructor(address _registryAddress) {
        registry = CrowdfundingRegistry(_registryAddress);
    }

    function createCampaign(
        string memory _title,
        uint256 _goal,
        uint256 _durationDays
    ) external {
        require(_goal > 0, "Goal must be greater than 0");
        require(_durationDays > 0, "Duration must be greater than 0");
        
        bytes32 campaignId = keccak256(
            abi.encodePacked(
                block.timestamp,
                msg.sender,
                _title,
                _goal
            )
        );
        
        campaigns[campaignId] = Campaign(
            payable(msg.sender),
            _title,
            _goal,
            block.timestamp + _durationDays * 1 days,
            0,
            false
        );
        
        emit CampaignCreated(
            campaignId,
            msg.sender,
            _title,
            _goal,
            block.timestamp + _durationDays * 1 days
        );
    }

    function contribute(bytes32 _campaignId) external payable {
        Campaign storage campaign = campaigns[_campaignId];
        require(block.timestamp < campaign.deadline, "Campaign has ended");
        require(msg.value > 0, "Contribution must be greater than 0");
        
        if (contributions[_campaignId][msg.sender] == 0) {
            backers[_campaignId].push(msg.sender);
        }
        
        campaign.amountRaised += msg.value;
        contributions[_campaignId][msg.sender] += msg.value;
        emit ContributionMade(_campaignId, msg.sender, msg.value);
    }

    function finalizeCampaign(bytes32 _campaignId) external nonReentrant {
        Campaign storage campaign = campaigns[_campaignId];
        require(block.timestamp >= campaign.deadline, "Campaign not ended yet");
        require(!campaign.fundsReleased, "Funds already handled");
        
        campaign.fundsReleased = true;

        if (campaign.amountRaised >= campaign.goal) {
            campaign.creator.transfer(campaign.amountRaised);
            emit FundsReleased(_campaignId, campaign.amountRaised);
        } else {
            address[] storage campaignBackers = backers[_campaignId];
            for (uint256 i = 0; i < campaignBackers.length; i++) {
                address backer = campaignBackers[i];
                uint256 amount = contributions[_campaignId][backer];
                if (amount > 0) {
                    pendingRefunds[_campaignId][backer] = amount;
                    contributions[_campaignId][backer] = 0;
                    emit RefundAvailable(_campaignId, backer, amount);
                }
            }
        }
    }

    function claimRefund(bytes32 _campaignId) external nonReentrant {
        uint256 amount = pendingRefunds[_campaignId][msg.sender];
        require(amount > 0, "No refund available");
        
        pendingRefunds[_campaignId][msg.sender] = 0;
        payable(msg.sender).transfer(amount);
    }

    // Helper functions
    function daysToSeconds(uint256 _days) public pure returns (uint256) {
        return _days * 1 days;
    }

    function getBackers(bytes32 _campaignId) public view returns (address[] memory) {
        return backers[_campaignId];
    }
}
